import React, { Component } from 'react';
import PersonalInfoForm from './PersonalInfoForm'
import SkillsForm from './SkillsForm'
import WorkHistoryForm from './WorkHistoryForm'
import EducationForm from './EducationForm'

class ResumeForm extends Component {

    constructor(){
        super()
        this.state = {
           resume: {}

        }
    }

    // addNewResume = () => {
    //     fetch('http://localhost:3000/resumes', {
    //         method: 'POST',
    //         headers: {"Content-Type": "application/json"},
    //     }) 
    //     .then(res => res.json())
    //     .then(resume => this.props.addNewResume(resume))
    // }


    componentDidMount(){
        fetch(`http://localhost:3000/resumes/${this.props.match.params.id}`)
        .then(res => res.json())
        .then(resume => {
            this.setState({
                resume: resume
            })
        })
    }

    // handleSubmit = (event) => {
    //     this.setState({
    //         personal_info: event.target.personal_info.value,
    //         work_history: event.target.work_history.value,
    //         education: event.target.education.value,
    //         skills: event.target.skills.value,

    //     }, () => this.addNewResume())
    // }

    getInfo = (part) => (value) => {
        this.setState({
            resume: {
                ...this.state.resume,
                [part]: value
            }
        })
     }



    render() {
        console.log(this.state.resume)
        return (
            <div>
               <PersonalInfoForm resume={this.state.resume.personal_info} getInfo={this.getInfo('personal_info')} />
               <WorkHistoryForm resume={this.state.resume}/>
               <EducationForm resume={this.state.resume}/>
               <SkillsForm resume={this.state.resume}/>

            </div>
        )
    }
}

export default ResumeForm;
